var webSocket;$().ready(function(){var n=window.location.host;webSocket=new WebSocket("ws://"+n+"/chat1");webSocket.onopen=function(){$("#spanStatus").text("connected")};webSocket.onmessage=function(n){$("#spanText").append("<hr />"+n.data)};webSocket.onerror=function(n){console.log(n);$("#spanText").append('<div class="alert alert-danger">'+n.message+"<\/div>")};webSocket.onclose=function(){console.log(arguments);var t=arguments,n="";typeof arguments!==undefined&&arguments.length>0&&typeof arguments[0].reason!="undefined"&&(n=" reason: "+arguments[0].reason);$("#spanStatus").text("disconnected."+n)};$("#btnSend").click(function(){webSocket.readyState==WebSocket.OPEN?webSocket.send($("#textInput").val()):$("#spanStatus").text("Connection is closed")})});