var webSocket;$().ready(function(){var n=window.location.host;webSocket=new WebSocket("ws://"+n+"/chat");webSocket.onopen=function(){$("#spanStatus").text("connected")};webSocket.onmessage=function(n){$("#spanText").append("<hr />"+n.data)};webSocket.onerror=function(n){console.log(n);$("#spanText").append('<div class="alert alert-danger">'+n.message+"<\/div>")};webSocket.onclose=function(){$("#spanStatus").text("disconnected")};$("#btnSend").click(function(){webSocket.readyState==WebSocket.OPEN?webSocket.send($("#textInput").val()):$("#spanStatus").text("Connection is closed")})});